const goodsSelect=document.querySelectorAll(".goods__select"),goodsBtn=document.querySelectorAll(".goods__btn"),goodsAmount=document.querySelectorAll(".goods__amount");function countBtn(){goodsBtn.forEach((o=>{o.addEventListener("click",(e=>{o.style.display="none";let t=o.closest(".goods").querySelector(".goods__amount");t.querySelector(".goods__amount-count span").innerHTML=1,t.style.display="flex"}))})),goodsAmount.forEach((o=>{let e=o.querySelector(".goods__amount-count span"),t=+e.innerText;o.addEventListener("click",(n=>{"goods__amount-plus"==n.target.dataset.name&&(t++,e.innerHTML=t),"goods__amount-minus"==n.target.dataset.name&&(t--,e.innerHTML=t,t<=0&&(t=1,o.style.display="none",o.closest(".goods").querySelector(".goods__btn").style.display="block",e.innerHTML=1))}))}))}goodsSelect.forEach((o=>{o.addEventListener("click",(()=>{o.classList.toggle("goods__select--active")}))})),countBtn();